--Inserta Bodegas
--Inserta Estantes

SELECT * 
FROM(
    SELECT idEstante as id_elemento,'ESTANTE' AS TIPO, CAPACIDADVOLUMEN / CAPACIDADTOTALVOLUMEN as INDICE_VOLUMEN, CAPACIDADPESO / CAPACIDADTOTALPESO as INDICE_PESO FROM ESTANTE WHERE idsucursal=100 UNION ALL SELECT idBodega as id_elemento,'BODEGA' AS TIPO,CAPACIDADVOLUMEN / CAPACIDADTOTALVOLUMEN AS INDICE_VOLUMEN, CAPACIDADPESO / CAPACIDADTOTALPESO as INDICE_PESO FROM BODEGA WHERE idsucursal=100
);

